// Defining the default options for the selectpicker plugin
!function(a) {
  a.fn.selectpicker.defaults = {

    // The text to display when no options are selected
    noneSelectedText: "Ничего не выбрано",

    // The text to display when no results are found
    noneResultsText: "Совпадений не найдено",

    // The text to display when some options are selected
    // {0} is the number of selected options, {1} is the total number of options
    countSelectedText: "Выбрано {0} из {1}",

    // The text to display when the maximum number of options is reached
    // {n} is the number of selected options, {var} is the singular or plural form of 'items'
    maxOptionsText: [
      "Достигнут предел ({n} {var} максимум)", // For single select with a maximum option limit
      "Достигнут предел в группе ({n} {var} максимум)", // For multiple selects within a group with a maximum option limit
      ["items", "item"] // The singular and plural forms of 'items'
    ],

    // The separator for multiple selected options
    multipleSeparator: ", "
  };
}(jQuery);
