// Adds a "print" plugin to the TinyMCE text editor
tinymce.PluginManager.add("print", function(e) {
  
    // Defines the "mcePrint" command, which will trigger the browser's print function
    e.addCommand("mcePrint", function() {
        e.getWin().print();
    });
    
    // Adds a "Print" button to the toolbar
    e.addButton("print", {
        title: "Print", // Tooltip text for the button
        cmd: "mcePrint" // Command to be executed when the button is clicked
    });
    
    // Adds a "Print" shortcut (Meta+P)
    e.addShortcut("Meta+P", "", "mcePrint");
    
    // Adds a "Print" menu item to the "file" context menu
    e.addMenuItem("print", {
        text: "Print", // Menu item text
        cmd: "mcePrint", // Command to be executed when the menu item is clicked
        icon: "print", // Menu item icon
        shortcut: "Meta+P", // Shortcut for the menu item
        context: "file" // Context menu to which the menu item belongs
    });
});
