// Adds a "print" plugin to the TinyMCE text editor
tinymce.PluginManager.add("print", (editor) => {
  const printCommand = () => {
    editor.getWin().print();
  };

  // Adds a "Print" button to the toolbar
  editor.ui.registry.addButton("print", {
    text: "Print",
    onAction: printCommand,
  });

  // Adds a "Print" shortcut (Meta+P)
  editor.ui.registry.addShortcut("printShortcut", {
    key: "Meta+P",
    exec: printCommand,
  });

  // Adds a "Print" menu item to the "file" context menu
  editor.ui.registry.addMenuItem("print", {
    text: "Print",
    onAction: printCommand,
    icon: "print",
    shortcut: "Meta+P",
    context: "file",
  });
});
